            'actionAble' => true,
            'quitDialog' => true,
            'text' => "为您播放{$keyword}",
            'type' => 'T'
        ];
        return $this;
    }

    public function setTotal($total) {
        $this->data['data']['result']['total'] = (int)$total;
    }

    public function setPlaylist(Playlist $playlist) {
        $newPlaylist = $playlist->getPlaylist();
        if (empty($newPlaylist)) {
            return $this;
        }
        $this->data['data']['result']['playlist'] = $newPlaylist;
        return $this;
    }

    public function getData() {
        return $this->data;
    }
}

class Playlist {
    private $playlist = [];

    public function setPlaylist(PlaylistItem $item) {
        $newItem = $item->getItem();
        if (empty($newItem['url'])) {
            return false;
        }
        array_push($this->playlist, $newItem);
    }

    public function getPlaylist() {
        return $this->playlist;
    }
}

class PlaylistItem {
    private $item = [];

    public function __construct()
    {
        mt_srand();
        $this->item['episode'] = mt_rand(1, 9999);
        $this->item['play_count'] = mt_rand(1, 9999);
        $this->item['tags'] = '';
        $this->item['duration'] = mt_rand(1, 9999);
        $this->item['update_time'] = date('Y-m-d H:i:s');
        $this->item['id'] = mt_rand(1, 9999);
    }

    public function setUrlM4aHigh($urlM4aHigh) {
        $this->item['url_m4a_high'] = $urlM4aHigh;
        return $this;
    }

    public function setUrlm4a($urlm4a) {
        $this->item['urlm4a'] = $urlm4a;
        return $this;
    }

    public function setTitle($title) {
        $this->item['title'] = $title;
        return $this;
    }

    public function setUrl($url) {
        $this->item['url'] = $url;
        return $this;
    }

    public function setCover($cover) {
        $this->item['cover'] = $cover;
        return $this;
    }

    public function setUrlHigh($urlHigh) {
        $this->item['url_high'] = $urlHigh;
        return $this;
    }

    public function setCoverLarge($coverLarge) {
        $this->item['cover_large'] = $coverLarge;
        return $this;
    }

    public function getItem() {
        return $this->item;
    }
}